import axios from 'axios';
export default function CreatePost(props){
    function createPost(postData) {
        axios.post('http://localhost:8000/posts', postData) // Replace with your server's URL and endpoint
          .then(response => console.log('Success:', response.data))
          .catch(error => console.error('Error:', error));
      }

    function handleSubmit(){
        let model=props.posts
        let newtitle=document.getElementById("post-title");
        let newtext=document.getElementById("post-text");
        let newPost={
            title:newtitle,
            content:newtext
        }
        createPost(newPost)
    }
    return (
        <div>
            <h1>Create Post</h1>
            <form onSubmit={()=>handleSubmit()}>
            <input type="text" id="post-title" />
            <input type="text" id="post-text"/>
            <button >Create post</button>
            </form>
        </div>
    )
}
