import axios from 'axios';
export default function PostPage(props){
    const generateComment=(e) =>{
        const com = e.target.value
        const postId = props.post._id
        const commentData={
            content: com
        }

        axios.post(`/comments/${postId}`, commentData)
        .then(response => {
            console.log('Comment created:', response.data);
            // You can update the UI to show the new comment
        })
        .catch(error => {
            console.error('Error creating comment:', error);
        });
    }
    
    return(
        <div>
            <div>
            <h1>{props.post.title}</h1>
            <p>{props.post.content}</p>
            </div>

            <div className="comments">
                <input type="text" onKeyUp={generateComment} placeholder="Comment..."/>
                <div className="post-comments">
                    <ul>
                        {(props.comments || []).filter((c) => {if(c._id in props.post.comments){return true} else{return false
                        }}).map(c => (
                            <div className="post-div">
                                <p>{c.content}</p>
                            <hr className="post-breaks"/>
                            </div>
                        ))}
                    </ul>
                </div> 
            </div>

            {/* <div className="post-comments">
                <ul>
                    {props.post.comments.map(post => (
                        <div className="post-div">
                            g Insert method to search using comment ID
                        <hr className="post-breaks"/>
                        </div>
                    ))}
                </ul>
            </div>  */}

        </div>
    )
    
}
